use nettle_sys::{
    sha3_256_ctx,
    nettle_sha3_256_init,
    nettle_sha3_256_digest,
    nettle_sha3_256_update,
    nettle_hash,
    nettle_sha3_256,
};
use std::default::Default;
use std::mem::zeroed;
use Hash;
use hash::NettleHash;

/// 256 bit variant of the Secure Hash Algorithm 3 (SHA-3) defined in FIPS 202.
#[allow(non_camel_case_types)]
pub struct Sha3_256 {
    context: sha3_256_ctx,
}

impl Clone for Sha3_256 {
    fn clone(&self) -> Self {
        use std::intrinsics::copy_nonoverlapping;

        unsafe {
            let mut ctx: sha3_256_ctx = zeroed();
            copy_nonoverlapping(&self.context, &mut ctx, 1);

            Sha3_256{ context: ctx }
        }
    }
}

impl Default for Sha3_256 {
    fn default() -> Self {
        let mut ctx = unsafe { zeroed() };

        unsafe { nettle_sha3_256_init(&mut ctx as *mut _); }

        Sha3_256{ context: ctx }
    }
}

impl Hash for Sha3_256 {
    fn digest_size(&self) -> usize { ::nettle_sys::SHA3_256_DIGEST_SIZE as usize }

    fn update(&mut self, data: &[u8]) {
        unsafe {
            nettle_sha3_256_update(&mut self.context as *mut _, data.len(), data.as_ptr());
        }
    }

    fn digest(&mut self, digest: &mut [u8]) {
        unsafe {
            nettle_sha3_256_digest(&mut self.context as *mut _, digest.len(), digest.as_mut_ptr());
        }
    }

    fn box_clone(&self) -> Box<Hash> {
        Box::new(self.clone())
    }
}

impl NettleHash for Sha3_256 {
    type Context = sha3_256_ctx;

    unsafe fn nettle_hash() -> &'static nettle_hash { &nettle_sha3_256 }
}

