use nettle_sys::{
    sha3_384_ctx,
    nettle_sha3_384_init,
    nettle_sha3_384_digest,
    nettle_sha3_384_update,
    nettle_hash,
    nettle_sha3_384,
};
use std::default::Default;
use std::mem::zeroed;
use Hash;
use hash::NettleHash;

#[allow(non_camel_case_types)]
/// 384 bit variant of the Secure Hash Algorithm 3 (SHA-3) defined in FIPS 202.
pub struct Sha3_384 {
    context: sha3_384_ctx,
}

impl Default for Sha3_384 {
    fn default() -> Self {
        let mut ctx = unsafe { zeroed() };

        unsafe { nettle_sha3_384_init(&mut ctx as *mut _); }

        Sha3_384{ context: ctx }
    }
}

impl Hash for Sha3_384 {
    fn digest_size(&self) -> usize { ::nettle_sys::SHA3_384_DIGEST_SIZE as usize }

    fn update(&mut self, data: &[u8]) {
        unsafe {
            nettle_sha3_384_update(&mut self.context as *mut _, data.len(), data.as_ptr());
        }
    }

    fn digest(&mut self, digest: &mut [u8]) {
        unsafe {
            nettle_sha3_384_digest(&mut self.context as *mut _, digest.len(), digest.as_mut_ptr());
        }
    }
}

impl NettleHash for Sha3_384 {
    type Context = sha3_384_ctx;

    unsafe fn nettle_hash() -> &'static nettle_hash { &nettle_sha3_384 }
}

